        {
            return null;
        }
        String jsonResult = null;
        try
        {
            jsonResult = objectMapper.writeValueAsString(obj);
        }
        catch (JsonProcessingException e)
        {

        }

        return jsonResult;
    }

    /**
     * 一般对象转JsonNode
     * @Description
     * @param obj
     * @return
     */
    public static JsonNode obj2node(Object obj)
    {
        if (null == obj)
        {
            return null;
        }

        JsonNode node = null;

        try
        {
            node = objectMapper.readTree(obj2json(obj));
        }
        catch (IOException e)
        {
        }

        return node;
    }

