        {
            t = objectMapper.readValue(obj2json(obj), clazz);
        }
        catch (IOException e)
        {
            e.printStackTrace();
        }

        return t;
    }

    /**
     * map convert to javaBean
     */
    private static <T> T map2pojo(@SuppressWarnings("rawtypes")Map map, Class<T> clazz)
    {
        return objectMapper.convertValue(map, clazz);
    }

}


